package chapter02;

public class ConstantLiteralMain {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		final double PI = 3.14;
		
		System.out.println(PI);
		
//		Pi = 3.141569; final은 변경불가
		
		float f = 1.0F;
		char c = 'A';
		int i = 1;
		long l = 1L;
		
		boolean b = true;
		
		//문자열 : char 배열
		String s = "Hello";

	}

}
