public class BooleanEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean b1 = false;
		boolean b2 = true;
		

		System.out.println("is true: " + true);
		System.out.println("is false: " + false);
		
		b1 = b2;
		System.out.println("b1: " + b1);

	}

}
